import React from 'react';
import {Card,CardActions,CardContent,CardMedia,Button,Typography} from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import moment from 'moment';
import useStyles from './style';

const Post = ({post})=>{  
    // console.log("aa"+post[0].tags[0]);
    console.log(post.tags)
    const classes = useStyles();
    // post.tags.map((arr)=>{
    //     return(
    //         <div>arr.tags[0]
    //         </div>
    //     )
    // })
    // }            
    const base64String = btoa(String.fromCharCode(...new Uint8Array(post.selectedFile)));
    console.log(base64String);

    return(
        <Card className={classes.card}>
            {/* <CardMedia className={classes.media} image={`data:image/png;base64,${base64String}`}  title={post.title}/> */}
            <CardMedia  className={classes.media} component='img' src={`data:image/jpg;base64, ${base64String}`}  title={post.title}/>
            <div className={classes.overlay}>
                <Typography variant="h6">{post.creator}</Typography>
                <Typography variant="body2">{moment(post.createdAt).fromNow()}</Typography>
            </div>
            <div className={classes.overlay2} >
                <Button style={{color:'white'}}size="small" onClick={()=>{}}>
                    <MoreHorizIcon fontSize="default"/>
                </Button>
            </div>

            <div className={classes.details}>
            <Typography variant="body2" color="textSecondary" component="h2">{post.tags.map((tag) => `#${tag} `)}</Typography>
            </div>
            <CardContent>
            <Typography className={classes.title} variant="h5" gutterBottom>{post.message}</Typography>
            </CardContent>
            <CardActions className={classes.cardActions}>
                <Button size="small" color="primary" onClick={()=>{}}>
                <ThumbUpAltIcon fontSize="small"/>
                Like
                {post.likeCount}
                </Button>
                <Button size="small" color="priamry" onClick={()=>{}}>
                <DeleteIcon fontSize="small"/>
                Delete
                </Button>
            </CardActions>
        </Card>
    );
}
export default Post;
